mapper.update:
  	UPDATE ARTICLE
	SET DEL=1
	WHERE SEQ=#{seq}

mapper.select:
SELECT * FROM (SELECT ROW_NUMBER() OVER(ORDER BY WDATE DESC) AS RNUM, SEQ,TITLE,CATEGORY,LIKEIT,WDATE,DEL FROM ARTICLE WHERE 1=1
AND TITLE LIKE '%'||#{search}||'%'
) A WHERE RNUM BETWEEN ${start} AND ${end} AND CATEGORY = #{category}
ORDER BY WDATE DESC
ORDER BY WDATE ASC
ORDER BY LIKEIT DESC

SELECT NVL(COUNT(*), 0) AS CNT FROM ARTICLE WHERE 1=1
AND TITLE LIKE '%'||#{search}||'%'
AND CATEGORY = #{category}

mapper.update:
  UPDATE ARTICLE 
	SET LIKEIT=LIKEIT+1 
 WHERE SEQ= #{seq}
