mapper.select:
SELECT ID, PWD, NAME, STORE, EMAIL, ADDRESS, PHONE, VAT_RATIO, DETAIL, IMAGE, AUTH
FROM MEMBER
WHERE ID=#{id} AND PWD=#{pwd}

SELECT IMAGE
FROM MEMBER
WHERE ID=#{id}

SELECT NVL(COUNT(*), 0)
FROM MEMBER
WHERE ID=#{id}

mapper.update:
UPDATE MEMBER SET
PWD=#{pwd}
WHERE ID=#{id}

mapper.select:
SELECT ID, PWD, NAME, STORE, EMAIL, ADDRESS, PHONE, VAT_RATIO, DETAIL, IMAGE, AUTH
FROM MEMBER
WHERE ID=#{id}

mapper.update:
UPDATE MEMBER
SET AUTH=2
WHERE ID=#{id}

mapper.select:
SELECT NVL(COUNT(*), 0)
FROM MEMBER
WHERE ID=#{id}
